        private void FaturaIslem_Load(object sender, EventArgs e)
        {
            var degerler = from u in db.TBLFATURABILGI
                           select new
                           {
                               u.Id,
                               u.SıraNo,
                               u.Seri,
                               u.Personel,
                               u.Tarih,
                               u.VergiDairesi,
                               u.Cari
                           };
            gridControl1.DataSource = degerler.ToList();
        }

        private void BtnKaydet_Click(object sender, EventArgs e)
        {
            TBLFATURABILGI t = new TBLFATURABILGI();
            t.Seri = (lookUpEdit3.EditValue.ToString());
            t.SıraNo = Convert.ToInt32(txtmarka.Text);
            t.Tarih= DateTime.Now;
            t.VergiDairesi = txtstok.Text;
            t.Cari = lookUpEdit1.EditValue.ToString();
            t.Personel = lookUpEdit4.EditValue.ToString();
            db.TBLFATURABILGI.Add(t);
            db.SaveChanges();
            MessageBox.Show("Fatura  başarıyla kaydedildi", "Bilgi", MessageBoxButtons.OK, MessageBoxIcon.Information);
        }

