        private void btnguncelle_Click(object sender, EventArgs e)
        {
            int id = int.Parse(textEdit1.Text);
            var deger = db.TBLFATURABILGI.Find(id);
            deger.Seri = (lookUpEdit3.EditValue.ToString());
            deger.SıraNo= Convert.ToInt32(txtmarka.Text);
            deger.Tarih = DateTime.Now;
            deger.Cari= lookUpEdit1.EditValue.ToString();
            deger.VergiDairesi = txtstok.Text;
            deger.Personel= lookUpEdit4.EditValue.ToString();
            db.SaveChanges();
            MessageBox.Show("Faturanız Güncellenmiştir", "Bilgi", MessageBoxButtons.OK, MessageBoxIcon.Warning);
        }

