        void metot1()
        {
            var degerler = from u in db.TBLURUN
                           select new
                           {
                               u.ID,
                               u.AD,
                               u.MARKA,
                               u.STOK,
                               KATEGORI = u.TBLKATEGORİ.AD,
                               u.ALISFIYAT,
                               u.SATISFIYAT
                               
        };
            gridControl1.DataSource = degerler.ToList();
        }
